mapper.update:
UPDATE tbl_bank SET

	money = money + #{money}


	money = money - #{money}

WHERE bno = #{bno}

mapper.delete:
DELETE
FROM tbl_bank
WHERE bno = #{bno} AND pw = #{pw}

mapper.select:
SELECT money
FROM tbl_bank
WHERE bno = #{bno}

SELECT COUNT(*)
FROM tbl_bank
WHERE bno = #{bno} AND pw = #{pw}
